mapper.insert:
insert into acg_person (personId , pId , name, nameCn, image, summary, height, weight, alias, type, gender, bloodtype, birthday, createTime, updateTime)
values (#{personId,jdbcType=INTEGER},#{pId,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{nameCn,jdbcType=VARCHAR}, #{image,jdbcType=VARCHAR}, #{summary,jdbcType=VARCHAR}
, #{height,jdbcType=VARCHAR}, #{weight,jdbcType=VARCHAR}, #{alias,jdbcType=VARCHAR}, #{type,jdbcType=VARCHAR}
, #{gender,jdbcType=INTEGER}, #{bloodtype,jdbcType=INTEGER}, #{birthday,jdbcType=VARCHAR}, #{createTime,jdbcType=INTEGER}, #{updateTime,jdbcType=INTEGER})

mapper.select:
select personId,pId,name,nameCn,image,height,weight,alias,gender,bloodtype,birthday from acg_person



            type like "%"#{type}"%"




        and gender = #{gender}


        and bloodtype = #{bloodtype}


        and birthday like "%"#{birthday}"%"
